import type { Palette } from '@mui/material';
declare const getScrollbarStyles: (palette: Palette) => {
    '::-webkit-scrollbar': {
        width: string;
        height: string;
    };
    '::-webkit-scrollbar-track': {
        background: string;
        borderRadius: string;
        overflow: string;
    };
    '::-webkit-scrollbar-thumb': {
        background: string;
        borderRadius: string;
    };
};
export default getScrollbarStyles;
